    // The method for calculating the boys function of a given pair of n (order) and x (position)

    public double boysFunction(int nPar, double xPar) {
        // Catch some undesired exceptions
        if (nPar > 30) {
            System.err.println("ERROR! The order n of the Boys function cannot be larger than 30!");
        } else if (xPar < 0) {
            System.err.println("ERROR! The argument x of the Boys function cannot be negative!");
        }

        // initialize the final function value
        double Fn = 0;

        double rootX = Math.sqrt(xPar);
        // If the order is zero, use the relation of the respective expression to the error function
        if (nPar == 0) {
            // If x is (numerically) zero, implement the simple 1/(2n+1) formula
            if (xPar <= 1E-14) {
                Fn = 1.0 / (2.0 * nPar + 1.0);
            } else {
                Fn = ROOTPI / (2 * rootX) * mathUtil.errorFunction(rootX);
            }
        } else {
            // If the order is greater than zero, do different actions depending on the value of the x parameter
            // perform the Taylor series expansion of order 30 around the nearest
            // reference point and do the downward recursion to end at the desired function order
            if (xPar <= 1E-14) {
                // If x is (numerically) zero, implement the simple 1/(2n+1) formula
                Fn = 1.0 / (2.0 * nPar + 1.0);
            } else if (xPar <= 0.05) {
                // For small x: perform the Taylor series around the origin
                for (int i = 1; i <= 15; i++) {
                    double act = 0;
                    // System.out.println( i + " " + Math.pow(xPar,i) + " " + mathUtil.fact(i) + " " + 1/(2*(i+nPar)+1));
                    act = Math.pow(xPar, i) / (2 * (i + (double) nPar) + 1);
                    // Apply the (-1)^k factor in the general Taylor series expression

                    if (Math.floorMod(i, 2) == 0) {

                        // In order to avoid problems with numerical stability, perform a successive division of the
                        // faculty in the denominator

                        for (int j = 1; j <= i; j++) {
                            act = act / j;
                        }
                        Fn = Fn + act;

                    } else {

                        for (int j = 1; j <= i; j++) {
                            act = act / j;
                        }
                        Fn = Fn - act;
                    }
                    //System.out.println("act" + act);
                }
                // Finally, correct the value by the lower border of the integral (t=0)
                Fn = Fn + 1/(2* (double) nPar +1);
            } else if (xPar <= 30) {
                // For medium sized x, perform the Taylor series expansion of order 30 around the nearest
                // reference point and do the downward recursion to end at the desired function order

                // first determine the correct dimension of the boys data array for the actual expansion
                double roundOff = Math.round(xPar * 10.0) / 10.0;

                int xInd = (int) (roundOff * 10 - 1);

                // perform the Taylor expansion for n = 30

                double F30 = this.boysData[xInd][0];

                double deltaX = roundOff-xPar;

                double deltaXi = deltaX;
                for (int i = 1; i < 7; i++) {
                    F30 = F30 + this.boysData[xInd][i] * deltaXi / faculties[i];
                    deltaXi = deltaXi*deltaX;
                }

                // Now, do the downward recursion from n=30 to the actual nPar value

                Fn = F30;
                double expX = Math.exp(-xPar);
                double twoX = 2 * xPar;

                for (int i = 29; i >= nPar; i--) {
                    Fn = (twoX * Fn + expX) / (2.0 * i +1);
                }

            } else {
                // For large x, calculate the asymptotic value of the Boys function
                Fn = mathUtil.factDouble(2 * nPar - 1) / Math.pow(2, nPar + 1) * ROOTPI /
                        Math.sqrt(Math.pow(xPar, 2 * nPar + 1));
            }


        }

        return Fn;

    }


